import React, { useState } from 'react';
import axios from 'axios';

export default function Contact() {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [status, setStatus] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/contact', formData);
      setStatus(res.data.msg);
    } catch (err) {
      setStatus('Error sending message');
    }
  };

  return (
    <section className="container my-5" id="contact">
      <h2 className="text-center mb-4">Contact Me</h2>
      <form onSubmit={handleSubmit} className="mx-auto" style={{ maxWidth: '500px' }}>
        <input type="text" name="name" placeholder="Your Name" className="form-control mb-3" onChange={handleChange} />
        <input type="email" name="email" placeholder="Your Email" className="form-control mb-3" onChange={handleChange} />
        <textarea name="message" placeholder="Your Message" className="form-control mb-3" onChange={handleChange}></textarea>
        <button type="submit" className="btn btn-success w-100">Send Message</button>
      </form>
      {status && <p className="text-center mt-3">{status}</p>}
    </section>
  );
}
