import React, { useState } from "react";
import axios from "axios";

export default function Contact() {
  const [form, setForm] = useState({ name: "", email: "", phone: "", message: "" });
  const [submittedData, setSubmittedData] = useState(null);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/contact", form);
      alert("‚úÖ Message sent successfully!");
      setSubmittedData(form); // form data state ‡¶è ‡¶∞‡¶æ‡¶ñ‡¶¨‡ßá
      setForm({ name: "", email: "", phone: "", message: "" });
    } catch (err) {
      alert("‚ùå Error sending message!");
    }
  };

  return (
    <section className="container my-5" id="contact">
      <h2 className="text-center mb-4 text-success">Contact Me</h2>
      <form onSubmit={handleSubmit} className="mx-auto" style={{ maxWidth: "600px" }}>
        <div className="mb-3">
          <input type="text" name="name" className="form-control" placeholder="Name"
            value={form.name} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <input type="email" name="email" className="form-control" placeholder="Email"
            value={form.email} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <input type="text" name="phone" className="form-control" placeholder="Phone"
            value={form.phone} onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <textarea name="message" className="form-control" placeholder="Message" rows="4"
            value={form.message} onChange={handleChange} required></textarea>
        </div>
        <button type="submit" className="btn btn-success w-100">Send Message</button>
      </form>

      {/* ‚úÖ Submitted data display */}
      {submittedData && (
        <div className="mt-4 p-3 border rounded bg-light">
          <h5>üì© Submitted Info:</h5>
          <p><strong>Name:</strong> {submittedData.name}</p>
          <p><strong>Email:</strong> {submittedData.email}</p>
          <p><strong>Phone:</strong> {submittedData.phone}</p>
        </div>
      )}
    </section>
  );
}
